import React, { useState } from 'react';
import { Container, Typography, List, ListItem, ListItemAvatar, Avatar, Box, ListItemText, TextField, Button } from '@mui/material';
import AndroidIcon from '@mui/icons-material/Android';
import PersonIcon from '@mui/icons-material/Person';
import SendIcon from '@mui/icons-material/Send';
import backgroundImage from './bg.jpg'; // Ensure the path is correct

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { sender: 'user', text: input }]);
      setInput('');
      // Simulate a bot response
      setTimeout(() => {
        setMessages((prevMessages) => [
          ...prevMessages,
          { sender: 'bot', text: 'This is a response from the bot.' }
        ]);
      }, 1000);
    }
  };

  return (
    <Container
      sx={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh', // Full viewport height
        width: '100vw', // Full viewport width
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden', // Hide overflow to remove scroll
        position: 'fixed', // Fix position to viewport
        top: 0,
        left: 0,
        padding: '20px', // Add some padding for inner content
        boxSizing: 'border-box'
      }}
    >
      <Box
        sx={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)', // Semi-transparent background
          borderRadius: '10px',
          width: '100%',
          maxWidth: '600px', // Set max width for the chat container
          height: '100%',
          maxHeight: '90vh', // Set max height for the chat container
          display: 'flex',
          flexDirection: 'column',
          padding: '20px',
          boxSizing: 'border-box',
          overflowY: 'auto' // Allow scrolling for the chat content
        }}
      >
        <Typography variant="h4" gutterBottom>
          Chat with Telco Bot
        </Typography>
        <List sx={{ flexGrow: 1, overflowY: 'auto' }}>
          {messages.map((message, index) => (
            <ListItem key={index} style={{ justifyContent: message.sender === 'user' ? 'flex-end' : 'flex-start' }}>
              {message.sender === 'bot' && (
                <ListItemAvatar>
                  <Avatar>
                    <AndroidIcon />
                  </Avatar>
                </ListItemAvatar>
              )}
              <Box
                sx={{
                  backgroundColor: message.sender === 'user' ? '#dcf8c6' : '#f1f0f0',
                  borderRadius: '20px',
                  padding: '10px',
                  maxWidth: '60%',
                }}
              >
                <ListItemText primary={message.text} />
              </Box>
              {message.sender === 'user' && (
                <ListItemAvatar>
                  <Avatar>
                    <PersonIcon />
                  </Avatar>
                </ListItemAvatar>
              )}
            </ListItem>
          ))}
        </List>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type your message here..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <Button
          variant="contained"
          color="primary"
          endIcon={<SendIcon />}
          onClick={handleSend}
          sx={{ marginTop: 2 }}
        >
          Send
        </Button>
      </Box>
    </Container>
  );
};

export default App;
